import React,{ useState } from "react";
import '../styling/cart.css';
function Cart() {

  const[Items,setItems] = useState([
    {
      id:1,
      count:1,
      name:"AetherteX titus Patheon 16GB",
      price: 2000 ,
      image: "data:image/jpeg;base64,",
    }
    ,
    {
      id:2,
      count:1,
      name:"AetherteX titus Exiler 8GB",
      price: 1000,
      image: "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcRnhFHsGWsZhvoFUW_izKpIvbGQLhvp3gbA8A7nbwSwyN0YI-zlRhgMy6t8davVnf_0w7PUZIu7mzgW-kaa0WUKO3AVn5IlcyWdN-Of_iFRbaHXaVDCjoTp5vPmwYza7Q",
    }
    ,
     {
      id:3,
       count:1,
      name:"AetherteX Athena blade 32GB",
      price: 10000,
      image: "data:image/jpeg;base64,",
    }
   
    ,
     {
      id:3,
       count:1,
      name:"AetherteX Athena blade 32GB",
      price: 10000,
      image: "data:image/jpeg;base64,",
    }
   
    ,
     {
      id:3,
       count:1,
      name:"AetherteX Athena blade 32GB",
      price: 10000,
      image: "data:image/jpeg;base64,",
    }
   
  ]); 
   
   

  function increaseCount(id) {
    setItems
    (
      Items.map
      (
        item=>
        {
          if(item.id==id)
          {
            return {...item, count :item.count+1}
          }
          return item;
        }
      )
    )
  }

    function decreaseCount(id) {
    setItems
    (
      Items.map
      (
        item=>
        {
          if(item.id==id)
          {
            return{...item, count: item.count > 0 ? item.count - 1 : 0 }
          }
          return item;
        }
      )
    )
  }
  function removeItem(id)
  {
    setItems
    (
      Items.filter
      (
        item=>item.id!=id
      )
    )
  }

  {/*calculations for billing*/}
  const subtotal = Items.reduce((acc, item) => acc + item.price * item.count, 0);
  const DeliveryFee = subtotal > 0 ? 100 : 0;
  const total = subtotal > 5100 ? subtotal : subtotal - DeliveryFee;
  const totalItems = Items.reduce((acc, item) => acc + item.count, 0);

  return (
  <div >
      <div style={{marginLeft:40}}>
      <h1>Cart - {totalItems}</h1>
       <p>Home/Cart</p>
      </div>
        
      <div className="palate">
   
        <div className="items">
    {Items.length !== 0 ?
    (
        Items.map
        (
            item =>
            {
                return <div key={item.id} className="cartItem">
                    <div className="itemImage" style={{marginLeft:20}}>
                        <img src={item.image}/>
                    </div>{/*image manipulating*/}

                    <div className="itemDetails" >
                      
                        <h3>{item.name}</h3>
                        <hr style={{width:300 }}/>
                        <div>
                            <div className="IncDecStyle" style={{marginTop:20}}>
                                <label id="Price" style={{marginLeft:30}}> R {item.price}</label>
                                 <button id="decrease" className="btnIncOrDec" style={{marginLeft:35}} onClick={() => decreaseCount(item.id)}>-</button>
                                  {item.count}
                                 <button id="increase" className="btnIncOrDec" onClick={() => increaseCount(item.id)}>+</button>
                            </div>

                            <button onClick={() => removeItem(item.id)} style={{fontSize:16, backgroundColor:"transparent", border:'none', color:"white",cursor:"pointer",marginTop:30}}>ðŸ—‘ Remove</button>
                        </div>
                       
                    </div>{/*items details*/}
                </div>
            }
        )
    ) 
    :
    (
        <p style={{padding:"20px", textAlign:"center",color:"black" ,backgroundColor:"tan"}}>Your cart is empty.</p>
    )}
</div>{/*left column*/}  

        <div className="billing">{/*right column*/}
            <h3 style={{marginRight:460}}>Price details</h3>
            <hr width="600px"/>
            <p style={{marginRight:200, marginTop:30}}><label htmlFor="items" style={{marginRight:30}}>No. Items </label><input id="items" value={"R "+total} /></p>

            <p style={{marginRight:200, marginTop:30}}><label htmlFor="savings" style={{marginRight:45}}>Savings </label><input id="savings" value="R 0"/></p>

            <p style={{marginRight:200, marginTop:30}}><label htmlFor="Subtotal" style={{marginRight:40}}>Subtotal </label><input id="Subtotal" value={"R "+subtotal}/></p>

            <p style={{marginRight:200, marginTop:30}}><label htmlFor="Delivery" style={{marginRight:15}}>Delivery fee </label><input id="Delivery" value={"R "+DeliveryFee}/></p>

            <p style={{marginRight:200, marginTop:30}}><b><label htmlFor="Total amount" style={{marginRight:7}}>Total Amount</label><input id="Total amount" value={"R "+total}/></b></p>

            <button className="btnCheckout">Checkout</button>
        </div>
        
      </div>
      
  </div>
  );
}

export default Cart;
